mapper.update:

update question
set unit_id = #{unitId,jdbcType=INTEGER},
answer = #{answer,jdbcType=CHAR},
content = #{content,jdbcType=LONGVARCHAR},
options = #{options,jdbcType=LONGVARCHAR}
where question_id = #{questionId,jdbcType=INTEGER}

mapper.select:

select question_id, unit_id, answer, content, options
from question
where question_id = #{questionId,jdbcType=INTEGER}
